mapper.select:
	
			SELECT * FROM (
				SELECT rownum rnum, a.*
				FROM (
					SELECT empinfo.* 
                      FROM EMPINFO empinfo
					WHERE eno > 0
					ORDER BY eno ASC
				) a
			)
		WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

SELECT * FROM ( SELECT rownum rnum, a.* FROM ( SELECT empinfo.* FROM EMPINFO empinfo WHERE eno > 0 ORDER BY eno ASC ) a
WHERE EMPLOYMENTTYPE LIKE '%'||#{keyword}||'%'
) WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

mapper.update:
UPDATE empinfo SET 
subclass = #{subclass}
WHERE title = #{title}
